	CString  szFileName[EVENTLOGS_CATEGORIES];
	DWORDLONG  fRetention[EVENTLOGS_CATEGORIES];
	CTimeSpan  tRetention[EVENTLOGS_CATEGORIES];
	UINT  nPolicy[EVENTLOGS_CATEGORIES];

