		m_cProfileInfo[0].SetBehaviorInfo(nCount, tInterval[0], Spinbox_GetPos(GetDlgItem(IDC_SETTINGS_NOTIFICATIONSPAGE_PAGERCALL_BEHAVIOR_ABORT_SECONDS)));
