	return((m_nPort[0] != m_nPort[1] || m_nBufferSize[0] != m_nBufferSize[1] || m_tTimeout[0] != m_tTimeout[1] || !m_pClients[0].Compare(&m_pClients[1]) || m_bEnabled[0] != m_bEnabled[1]) ? TRUE : FALSE);
