		if (SendDlgItemMessage(IDC_TELEMETRYEVENTS_IMPORT_LIST, LB_FINDSTRINGEXACT, (WPARAM)-1, (LPARAM)(LPCTSTR)Combobox_GetText(GetDlgItem(IDC_TELEMETRYEVENTS_IMPORT_DIRECTORY), nEvent)) < 0) break;
