		GetDlgItem(IDC_TELECOMMANDSERVICE_DEVICE_REMOVE)->EnableWindow((SendDlgItemMessage(IDC_TELECOMMANDSERVICE_DEVICE, LB_GETCURSEL) >= 0) ? CheckAccountPrivilege(PRIVILEGE_TELECOMMANDSERVICE_CHANGE) : FALSE);
