		CollectLogEntry(pEntries, STRING(IDS_TELECOMMANDOPTIONS_LOGOBJECT_COMMANDHANDLER), szCommandHandler, STRING(IDS_TELECOMMANDOPTIONS_LOGDESCRIPTION_MAXIMUMEXECUTIONTIMEPERIODCHANGE), szLogEntry, tLogEntry);
