	return((m_nCode[0] != m_nCode[1] || m_tStartTime[0] != m_tStartTime[1] || m_tStopTime[0] != m_tStopTime[1] || !m_pDevices[0].Compare(&m_pDevices[1])) ? TRUE : FALSE);
