	GetDlgItem(IDC_TOOLSDOWNLOADS_SCHEDULEPAGE_RUN_EVENT)->EnableWindow((SendDlgItemMessage(IDC_TOOLSDOWNLOADS_SCHEDULEPAGE_RUN_EVENT_NAME, CB_GETCOUNT) > 0) ? TRUE : FALSE);
